package algo_day_12;


public class PatternBrute {
	public static void main(String[] args) {
		String str = "";
		String pattern = "ATTATTTCTAATGACTTTGAAATTTTATATTTATTATTCTACACTTTATTATTAAAACTACATGCAATCAATGCCGCTAAGG";
		long start = System.currentTimeMillis();
		System.out.println(match(str, pattern, 0));
		long end = System.currentTimeMillis();
		System.out.println(end - start);
	}
	static int match(String text, String pattern,int start) {
		int i= start,j = 0;
		while(j < pattern.length() && i < text.length()) {
			if( text.charAt(i) != pattern.charAt(j)) {
				i = i - j;
				j = -1;
			}
			i++;
			j++;
		}
		if(j == pattern.length())
			return i-pattern.length();
		else
			return -1;		
	}
}
